import React from 'react';
import Tenzies from './components/Tenzies.js'
import Die from './components/Die.js'
import {nanoid} from 'nanoid'
import Confetti from 'react-confetti'
import Stopwatch from './components/Stopwatch.js'


function App() {

  const [viewSetting, setViewSettings] = React.useState(false)
  const [difficulty, setDifficulty] = React.useState(1)

  function handleClick(){
    setViewSettings(prev => !prev)
  }

  function Settings(){
    return(
      <div className="settings">
        <h2>Difficulty</h2>
        <div>
          <button value={1}>Easy</button>
          <button value={2}>Medium</button>
          <button value={3}>Hard</button>
        </div>
      </div>
    )
  }

  return(
    <div className="app">
      <div className="settings-button" onClick={handleClick}>{viewSetting?"Back":<span className="settings-span"><span className="settings-gear">&#9881;</span>Settings</span>}</div>
      {viewSetting?<Settings />:<Tenzies />}
    </div>
  )
}

export default App;
