import React from 'react';
import Die from './components/Die.js'

/**
 * Challenge: Update the array of numbers in state to be
 * an array of objects instead. Each object should look like:
 * { value: <random number>, isHeld: false }
 *
 * Making this change will break parts of our code, so make
 * sure to update things so we're back to a working state
 */
function App() {

  const [diceArr, setDiceArr] = React.useState(allNewDice())

  function allNewDice() {
    const diceArray = []
    let i = 0;
    while (i < 10){
      diceArray.push({
        value: Math.floor(Math.random() * 6)+1,
        isHeld: false
      });
      i++;
    }
    return diceArray;
  }

  function rollDice() {
    setDiceArr(diceArr.map(dice => !dice.isHeld ? ({...dice, value:Math.floor(Math.random() * 6)+1}) : {...dice}));
  }

  function setTrue(){
    console.log("Set true");
    setDiceArr(prevArr => prevArr.map(dice => ({...dice, isHeld: true})))
  }

  function print(){
    console.log("hi")
  }

  const dies = diceArr.map((dice,index) => <Die eventHandle={setTrue} key={100+index} value={dice.value} />)

  return (
    <main className="main-app">
      <div className="die-container">
        {dies}
      </div>
      <button onClick={rollDice} className="roll-die-btn">Roll</button>
    </main>
  );
}

export default App;
