import React from 'react'
import './leaderboard.css'
import { useNavigate } from 'react-router-dom'


const Leaderboards = () => {

  //api call for easy, medium, hard, and extreme games difficulties
  const [matchDiffFilter, setMatchDiffFilter] = React.useState('easy')
  const [savedTimes, setSavedTimes] = React.useState([
    {
      dif: 'hard',
      time: 5000,
      user: 'kyle'
    },
    {
      dif: 'hard',
      time: 5500,
      user: 'john'
    },
    {
      dif: 'hard',
      time: 7000,
      user: 'phil'
    },
    {
      dif: 'hard',
      time: 5000,
      user: 'kyle'
    },
    {
      dif: 'hard',
      time: 5500,
      user: 'john'
    },
    {
      dif: 'hard',
      time: 7000,
      user: 'phil'
    },
    {
      dif: 'easy',
      time: 7000,
      user: 'phil'
    },
    {
      dif: 'extreme',
      time: 7000,
      user: 'phil'
    }
  ])
  const navigate = useNavigate()

  //Event functions
  const goBack = () => {
    navigate('/');
  }

  const handleChange = (e) => {
    setMatchDiffFilter(e.target.value)
  }

  const tableData = savedTimes
    .filter(match => {
      return match.dif === matchDiffFilter
    })
    .map((match,index) => {
    return (
      <tr className="table-row" key={match.id}>
        <td>{index+1}</td>
        <td>{match.user}</td>
        <td>{match.dif}</td>
        <td>
          <span>{("0" + Math.floor((match.time / 60000) % 60)).slice(-2)}:</span>
          <span>{("0" + Math.floor((match.time / 1000) % 60)).slice(-2)}:</span>
          <span>{("0" + ((match.time / 10) % 100)).slice(-2)}</span>
        </td>
      </tr>
    )
  })

  return (
    <div className="leaderboards">
      <div onClick={goBack} className="back">&#8592; Back</div>
      <h1>Leaderboards</h1>
      <div className="leaderboard-filter">
        <select onChange={handleChange}>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
          <option value="extreme">Extreme</option>
        </select>
      </div>
      <div className="table-container">
        <table className="best-game-table">
          <thead>
            <tr>
              <th>Rank</th>
              <th>Name</th>
              <th>Difficulty</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>{tableData}</tbody>
        </table>
      </div>

    </div>
  )
}

export default Leaderboards;
